INSERT INTO `o_reports` VALUES (1,'Incoming Payments','List of all payments coming in','SELECT o_incoming_payments.uid,  o_incoming_payments.amount, o_incoming_payments.comments, o_incoming_payments.mobile_number, o_incoming_payments.transaction_code, o_incoming_payments.loan_id, o_incoming_payments.loan_balance, o_incoming_payments.payment_date, o_incoming_payments.record_method, o_incoming_payments.comments, o_payment_statuses.name as status   FROM o_incoming_payments left join o_payment_statuses on o_payment_statuses.uid = o_incoming_payments.status where o_incoming_payments.status >= 1  AND o_incoming_payments.payment_date BETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_incoming_payments.uid,  o_incoming_payments.amount, o_incoming_payments.comments, o_incoming_payments.mobile_number, o_incoming_payments.transaction_code, o_incoming_payments.loan_id, o_incoming_payments.loan_balance, o_incoming_payments.payment_date, o_incoming_payments.record_method, o_incoming_payments.comments, o_payment_statuses.name as status   FROM o_incoming_payments left join o_payment_statuses on o_payment_statuses.uid = o_incoming_payments.status where o_incoming_payments.status >= 1  AND o_incoming_payments.payment_date BETWEEN {{{start_date}}} AND {{{end_date}}} AND o_incoming_payments.branch_id = {{{branch}}};',1,'2021-06-18 18:23:30','0',1),(3,'Loan Disbursement Reports','Show loans disbursed in a set period','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, o_loans.total_repayable_amount as Amount_Repayable, o_loans.total_repaid as Amount_Repaid, o_loans.loan_balance as Balance, o_loans.given_date as Disbursement_Date, \no_loans.final_due_date as Repayment_Date, lo.name as LO, co.name as CO, o_loans.application_mode, o_loan_statuses.name as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.status != 0 AND o_loans.disbursed=1 AND o_loans.given_date  BETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, o_loans.total_repayable_amount as Amount_Repayable, o_loans.total_repaid as Amount_Repaid, o_loans.loan_balance as Balance, o_loans.given_date as Disbursement_Date, \no_loans.final_due_date as Repayment_Date, lo.name as LO, co.name as CO, o_loans.application_mode, o_loan_statuses.name as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.status != 0 AND o_loans.disbursed=1 AND o_loans.given_date  BETWEEN {{{start_date}}} AND {{{end_date}}} AND o_loans.current_branch = {{{branch}}};',51,'2021-08-08 12:00:26','0',1),(4,'Customer Acquisition','List of customers who have joined the system in a given time period','SELECT o_customers.uid, o_customers.full_name, o_customers.primary_mobile, o_branches.name as branch_name, o_users.name as Added_by,  o_customers.physical_address, o_customers.national_id, o_customers.dob, o_customers.added_date, o_customers.loan_limit FROM o_customers left join o_branches on o_branches.uid = o_customers.branch left join o_users on o_users.uid = o_customers.added_by where DATE(o_customers.added_date) between {{{start_date}}} AND {{{end_date}}} AND o_customers.status = 1;','SELECT o_customers.uid, o_customers.full_name, o_customers.primary_mobile, o_branches.name as branch_name, o_users.name as Added_by,  o_customers.physical_address, o_customers.national_id, o_customers.dob, o_customers.added_date, o_customers.loan_limit FROM o_customers left join o_branches on o_branches.uid = o_customers.branch left join o_users on o_users.uid = o_customers.added_by where DATE(o_customers.added_date) between {{{start_date}}} AND {{{end_date}}} AND o_customers.status = 1 AND o_customers.branch = {{{branch}}};',1,'2022-01-11 16:05:14','',1),(5,'Defaulters Report','Show a list of defaulters by date they defaulted','SELECT o_loans.uid, o_customers.full_name, o_customers.national_id, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, o_loans.total_repayable_amount as TotalAmount, o_loans.total_repaid as Total_paid, o_loans.loan_balance as Balance, o_loans.transaction_code as Transcode, o_loans.given_date as Disbursement_Date, o_loans.final_due_date as Due_Date, \n o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, lo.name as LO, co.name as CO, o_loan_statuses.name as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.paid = 0 AND o_loans.status NOT IN (0, 10) AND o_loans.final_due_date  BETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_loans.uid, o_customers.full_name, o_customers.national_id, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, o_loans.total_repayable_amount as TotalAmount, o_loans.total_repaid as Total_paid, o_loans.loan_balance as Balance, o_loans.transaction_code as Transcode, o_loans.given_date as Disbursement_Date, o_loans.final_due_date as Due_Date, \n o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, lo.name as LO, co.name as CO, o_loan_statuses.name as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.paid = 0 AND o_loans.status NOT IN (0, 10) AND o_loans.final_due_date  BETWEEN {{{start_date}}} AND {{{end_date}}} AND o_loans.current_branch = {{{branch}}};',1,'2022-03-25 10:31:57','',1),(6,'Loans Falling Due','Show a list of Loans falling due in a particular date','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, o_loans.total_repayable_amount as TotalAmount, o_loans.total_repaid as Total_paid, o_loans.loan_balance as Balance, o_loans.transaction_code as Transcode, o_loans.given_date as Disbursement_Date, o_loans.final_due_date as Due_Date, \n o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, lo.name as LO, co.name as CO, o_loan_statuses.name as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.status != 0 AND o_loans.final_due_date  BETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, o_loans.total_repayable_amount as TotalAmount, o_loans.total_repaid as Total_paid, o_loans.loan_balance as Balance, o_loans.transaction_code as Transcode, o_loans.given_date as Disbursement_Date, o_loans.final_due_date as Due_Date, \n o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, lo.name as LO, co.name as CO, o_loan_statuses.name as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.status != 0 AND o_loans.final_due_date  BETWEEN {{{start_date}}} AND {{{end_date}}} AND o_loans.current_branch={{{branch}}};',1,'2022-03-25 10:40:15','',1),(7,'Missing Customer Accounts','Accounts that may have been deleted by mistake','SELECT * from o_customers WHERE status = 0 ;',NULL,1,'2022-05-09 16:50:55','',1),(8,'Bulk SMS','Show a List of outgoing SMS','SELECT uid, phone, message_body, queued_date as date_created, status FROM o_sms_outgoing WHERE DATE (queued_date) BETWEEN {{{start_date}}} AND {{{end_date}}};','',1,'2022-05-10 16:08:33','',1),(9,'Dormant Customers','List of customers who have limits but have not borrowed','SELECT c.uid, c.full_name, c.primary_mobile, c.national_id, c.gender, c.loan_limit, c.added_date, b.name as branch from o_customers c left join o_branches\n b on c.branch = b.uid where c.loan_limit >= 0 AND c.status = 1 AND not exists (select uid from o_loans l where l.customer_id = c.uid \nAND l.disbursed = 1  AND l.paid = 0 );',NULL,1,'2022-06-23 13:22:23','',0),(10,'Leads','List of all leads','SELECT o_customers.uid, o_customers.full_name, o_customers.primary_mobile, o_branches.name as branch_name, o_users.name as Added_by,  o_customers.physical_address, o_customers.national_id, o_customers.dob, o_customers.added_date, json_extract(o_customers.sec_data, \'$.\"51\"\') as lead_type FROM o_customers left join o_branches on o_branches.uid = o_customers.branch left join o_users on o_users.uid = o_customers.added_by where DATE(o_customers.added_date) between {{{start_date}}} AND {{{end_date}}} AND o_customers.status = 3;','SELECT o_customers.uid, o_customers.full_name, o_customers.primary_mobile, o_branches.name as branch_name, o_users.name as Added_by,  o_customers.physical_address, o_customers.national_id, o_customers.dob, o_customers.added_date, json_extract(o_customers.sec_data, \'$.\"51\"\') as lead_type FROM o_customers left join o_branches on o_branches.uid = o_customers.branch left join o_users on o_users.uid = o_customers.added_by where DATE(o_customers.added_date) between {{{start_date}}} AND {{{end_date}}} AND o_customers.status = 3 AND o_customers.branch={{{branch}}};',1,'2022-08-25 10:39:01','',1),(11,'Agent Collections','A list of collections made by agents','SELECT o_incoming_payments.uid as payment_id, o_incoming_payments.amount as payment_amount, o_incoming_payments.payment_date as payment_date,  o_incoming_payments.transaction_code \nas Mpesa_code, o_incoming_payments.loan_id as Loan_id, o_loans.loan_amount as loan_amount, o_loans.given_date as loan_given_date, o_loans.final_due_date as loan_due_date, \no_loans.loan_balance as loan_balance, o_users.name as agent_name, o_users.email as agent_email, o_branches.name as branch_name, o_loan_statuses.name as loan_status FROM \nmaria_simple.o_incoming_payments\n  left join o_loans on \no_loans.uid = o_incoming_payments.loan_id left join o_customers on o_customers.uid = o_loans.customer_id  left join o_users\n on o_users.uid = o_incoming_payments.added_by left join o_branches on o_branches.uid = o_incoming_payments.branch_id \n left join o_loan_statuses on o_loan_statuses.uid = o_loans.status where o_incoming_payments.status = 1 AND o_users.status = 1 AND o_incoming_payments.payment_date between {{{start_date}}} AND {{{end_date}}} order by o_incoming_payments.payment_date desc;',NULL,1,'2022-09-23 15:12:13','',1),(12,'Staff Codes','All staff members and their code','SELECT uid as code, name from o_users where status=1 AND branch > 1 ;',NULL,1,'2022-10-05 16:59:42','',1),(13,'Staff List','List of all staff','SELECT o_users.uid, o_users.name, o_users.email, o_users.phone, o_users.join_date , o_users.tag, o_staff_statuses.name as status,\n o_branches.name as branch FROM o_users left join o_user_groups on o_user_groups.uid = o_users.user_group left join o_branches \n on o_branches.uid = o_users.branch left join o_staff_statuses on o_staff_statuses.uid = o_users.status;',NULL,1,'2022-10-19 15:32:11','',1),(14,'Queued Loans','Loans Pending Disbursement by system','SELECT o_mpesa_queues.uid, o_mpesa_queues.loan_id, o_customers.full_name as customer_name ,o_loans.account_number as mobile_number , o_loans.customer_id as cid, \n o_mpesa_queues.amount, o_mpesa_queues.added_date, o_mpesa_queues.status FROM maria_simple.o_mpesa_queues \nleft join o_loans on o_loans.uid = o_mpesa_queues.loan_id left\njoin o_customers on o_customers.uid = o_loans.customer_id where o_mpesa_queues.status=1  order by uid desc;',NULL,1,'2023-03-03 00:00:00',' ',1),(15,'Rejected Loans','All the loans rejected during a specified period','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal,\no_loans.transaction_code as Transcode, o_loans.given_date \nas Disbursement_Date,  lo.name as LO, co.name as CO, o_loans.application_mode FROM o_loans \nleft join o_customers on o_customers.uid = o_loans.customer_id \nleft join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co \non o_loans.current_co = co.uid  where o_loans.status = 6  AND o_loans.given_date  \nBETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal,\no_loans.transaction_code as Transcode, o_loans.given_date \nas Disbursement_Date,  lo.name as LO, co.name as CO, o_loans.application_mode FROM o_loans \nleft join o_customers on o_customers.uid = o_loans.customer_id \nleft join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co \non o_loans.current_co = co.uid  where o_loans.status = 6  AND o_loans.given_date  \nBETWEEN {{{start_date}}} AND {{{end_date}}} AND o_loans.current_branch = {{{branch}}};',1,'2023-03-09 00:00:00',' ',1),(16,'Pending Disbursement','Loans pending disbursement','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal,\no_loans.transaction_code as Transcode, o_loans.given_date \nas Disbursement_Date,  lo.name as LO, co.name as CO, o_loans.application_mode FROM o_loans \nleft join o_customers on o_customers.uid = o_loans.customer_id \nleft join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co \non o_loans.current_co = co.uid  where o_loans.status = 2  AND o_loans.given_date  \nBETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal,\no_loans.transaction_code as Transcode, o_loans.given_date \nas Disbursement_Date,  lo.name as LO, co.name as CO, o_loans.application_mode FROM o_loans \nleft join o_customers on o_customers.uid = o_loans.customer_id \nleft join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co \non o_loans.current_co = co.uid  where o_loans.status = 2  AND o_loans.given_date  \nBETWEEN {{{start_date}}} AND {{{end_date}}} AND o_loans.current_branch = {{{branch}}};',1,'2023-03-09 00:00:00',' ',1),(17,'Partial Payments Report','Show a list of accounts and their partial payments','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, \no_loans.total_repayable_amount as TotalAmount, o_loans.total_repaid, o_loans.loan_balance as Balance,\n concat(round((o_loans.total_repaid / NULLIF(o_loans.total_repayable_amount, 0)) * 100, 2),\'%\') AS Rate, o_loans.transaction_code as Transcode, \n o_loans.given_date as Disbursement_Date, \no_loans.final_due_date as Repayment_Date,  lo.name as LO, co.name as CO, o_loan_statuses.name as Status \nFROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid \nleft join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co \non o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.status != 0 \nAND o_loans.disbursed=1 AND o_loans.given_date BETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, \no_loans.total_repayable_amount as TotalAmount, o_loans.total_repaid, o_loans.loan_balance as Balance,\n concat(round((o_loans.total_repaid / NULLIF(o_loans.total_repayable_amount, 0)) * 100, 2),\'%\') AS Rate, o_loans.transaction_code as Transcode, \n o_loans.given_date as Disbursement_Date, \no_loans.final_due_date as Repayment_Date,  lo.name as LO, co.name as CO, o_loan_statuses.name as Status \nFROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on o_loans.status = o_loan_statuses.uid \nleft join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co \non o_loans.current_co = co.uid  where o_loans.disbursed = 1 AND o_loans.status != 0 \nAND o_loans.disbursed=1 AND o_loans.given_date BETWEEN {{{start_date}}} AND {{{end_date}}} AND o_loans.current_branch = {{{branch}}};',1,'2023-03-28 10:58:20','',1),(18,'Unallocated Payments','List of payments that have not been allocated','SELECT o_incoming_payments.uid,  o_incoming_payments.amount, o_incoming_payments.comments, o_incoming_payments.mobile_number, o_incoming_payments.transaction_code, o_incoming_payments.loan_id, o_incoming_payments.payment_date  FROM o_incoming_payments  where o_incoming_payments.status = 1 AND o_incoming_payments.loan_id = 0 AND o_incoming_payments.payment_date BETWEEN {{{start_date}}} AND {{{end_date}}};',NULL,1,'2023-04-05 12:28:42','',1),(19,'Collected By Agent','List of payments collected by CC/FA/EDC Agents','SELECT o_incoming_payments.uid as pay_id, o_customers.full_name as CustomerName, o_loans.account_number as mobile_number, o_branches.name as Branch, o_incoming_payments.amount, o_incoming_payments.transaction_code, o_incoming_payments.loan_id,\no_incoming_payments.payment_date, o_users.name as AgentName, o_loans.allocation  FROM maria_simple.o_incoming_payments left join o_customers on o_customers.uid = o_incoming_payments.customer_id left join o_branches on o_branches.uid = o_incoming_payments.branch_id\nleft join o_users on o_users.uid = o_incoming_payments.collected_by left join o_loans on o_loans.uid = o_incoming_payments.loan_id where o_incoming_payments.status = 1 AND o_loans.allocation in (\'CC\',\'FA\',\'EDC\') AND o_incoming_payments.payment_date BETWEEN {{{start_date}}} AND {{{end_date}}} order by o_incoming_payments.uid desc, o_loans.allocation;','SELECT o_incoming_payments.uid as pay_id, o_customers.full_name as CustomerName, o_loans.account_number as mobile_number, o_branches.name as Branch, o_incoming_payments.amount, o_incoming_payments.transaction_code, o_incoming_payments.loan_id,\no_incoming_payments.payment_date, o_users.name as AgentName, o_loans.allocation  FROM maria_simple.o_incoming_payments left join o_customers on o_customers.uid = o_incoming_payments.customer_id left join o_branches on o_branches.uid = o_incoming_payments.branch_id\nleft join o_users on o_users.uid = o_incoming_payments.collected_by left join o_loans on o_loans.uid = o_incoming_payments.loan_id where o_incoming_payments.status = 1 AND o_loans.allocation in (\'CC\',\'FA\',\'EDC\') AND o_incoming_payments.payment_date BETWEEN {{{start_date}}} AND {{{end_date}}} order by o_incoming_payments.uid desc, o_loans.allocation;',1,'2023-05-08 16:37:29','',1),(20,'Deleted and Rejected Loans','List of loans Deleted or Rejected','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, o_loans.total_repayable_amount as TotalAmount, o_loans.loan_balance as Balance, o_loans.transaction_code as Transcode, o_loans.given_date as Disbursement_Date, \no_loans.final_due_date as Repayment_Date, o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, lo.name as LO, co.name as CO, o_loans.application_mode, COALESCE(o_loan_statuses.name ,\'Deleted\') as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on  o_loans.status  = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where  o_loans.status IN (0,6) AND o_loans.given_date  BETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_loans.uid, o_customers.full_name, o_branches.name as Branch , o_loans.account_number as Phone, o_loans.loan_amount as Principal, o_loans.total_repayable_amount as TotalAmount, o_loans.loan_balance as Balance, o_loans.transaction_code as Transcode, o_loans.given_date as Disbursement_Date, \no_loans.final_due_date as Repayment_Date, o_loans.disbursed_amount as Amount_Disbursed, o_loans.total_addons as Interest, lo.name as LO, co.name as CO, o_loans.application_mode, COALESCE(o_loan_statuses.name ,\'Deleted\') as Status FROM o_loans left join o_customers on o_customers.uid = o_loans.customer_id left join o_loan_statuses on  o_loans.status  = o_loan_statuses.uid left join o_branches on o_loans.current_branch = o_branches.uid left join o_users lo on o_loans.current_lo = lo.uid  left join o_users co on o_loans.current_co = co.uid  where  o_loans.status IN (0,6) AND o_loans.given_date  BETWEEN {{{start_date}}} AND {{{end_date}}} AND o_loans.current_branch = {{{branch}}};',1,'2023-05-12 15:01:44','',1),(21,'Logs','List of logs','SELECT o_events.uid, o_events.tbl, o_events.fld, o_events.event_details, o_events.event_date FROM maria_simple.o_events WHERE DATE(event_date) between {{{start_date}}} AND {{{end_date}}} AND status=1;',NULL,1,'2023-06-05 16:10:47','',0),(22,'Payments with customer ID and not Loan ID','List of payments with a customer ID but no loan ID','SELECT o_incoming_payments.uid,  o_incoming_payments.amount, o_incoming_payments.comments, o_incoming_payments.mobile_number, o_incoming_payments.transaction_code, o_incoming_payments.loan_id, o_incoming_payments.loan_balance, o_incoming_payments.payment_date, o_payment_statuses.name as status   FROM o_incoming_payments left join o_payment_statuses on o_payment_statuses.uid = o_incoming_payments.status where o_incoming_payments.status >= 1 AND customer_id > 0 AND loan_id = 0  AND o_incoming_payments.payment_date BETWEEN {{{start_date}}} AND {{{end_date}}};','SELECT o_incoming_payments.uid,  o_incoming_payments.amount, o_incoming_payments.comments, o_incoming_payments.mobile_number, o_incoming_payments.transaction_code, o_incoming_payments.loan_id, o_incoming_payments.loan_balance, o_incoming_payments.payment_date, o_payment_statuses.name as status   FROM o_incoming_payments left join o_payment_statuses on o_payment_statuses.uid = o_incoming_payments.status where o_incoming_payments.status >= 1 AND customer_id > 0 AND loan_id = 0  AND o_incoming_payments.payment_date BETWEEN {{{start_date}}} AND {{{end_date}}} AND o_incoming_payments.branch_id = {{{branch}}};',1,'2023-08-02 17:19:07','',1),(23,'Staff Member Logs','Shows a list of logs staff members','SELECT \n    *\nFROM\n    o_events\nWHERE\n    event_by = {{staff_id}}\n        AND event_date BETWEEN {{start_date}} AND {{end_date}}\nORDER BY uid DESC;\n',NULL,1,'2023-10-30 10:04:23',NULL,0),(24,'SMS Outgoing','List of outgoing messages','SELECT o_sms_outgoing.uid, o_customers.full_name, o_sms_outgoing.phone, o_sms_outgoing.message_body, o_sms_outgoing.queued_date \nas initiated_date, o_sms_outgoing.sent_date, CASE\n        WHEN o_sms_outgoing.status = 1 THEN \'Queued\'\n        WHEN o_sms_outgoing.status = 2 THEN \'Sent\'\n        ELSE \'Unknown\'\n    END AS status\n FROM Pembeni_db.o_sms_outgoing  left join o_customers on o_customers.primary_mobile = o_sms_outgoing.phone \n where o_sms_outgoing.status != 0 AND date(o_sms_outgoing.queued_date) between  {{{start_date}}} AND {{{end_date}}};',NULL,1,'2023-10-30 10:04:23',NULL,1);

